mapper.select:
select *
from setmeal


        and name like concat('%', #{name}, '%')


        and category_id = #{categoryId}


        and status = #{status}

select s.id, s.category_id, s.name, s.price, s.status, s.description, s.image, s.update_time, c.name categoryName from setmeal s left join category c on s.category_id = c.id
s.name like concat('%', #{name}, '%')
and s.category_id = #{categoryId}
and s.status = #{status}
